mapper.select:
select h.`status`, h.rfq_or_tender_form_code, h.title, h.quotation_start_time, h.quotation_stop_time, s.name supplierName, h.supplier_code supplierCode, h.principal, h.sourcing_type, h.way_of_quoting, h.way_to_find_the_source, h.currency, h.creator, h.company_code, t.companyName companyName from c_srm_enquiry_invitation_for_bids_t_h h left join c_srm_supplier s on h.supplier_code = s.supplier_code join c_mes_company_t t on t.companyCode = h.company_code
h.rfq_or_tender_form_code like concat('%',#{rfqOrTenderFormCode,jdbcType=VARCHAR},'%')
and h.title like concat('%',#{title,jdbcType=VARCHAR},'%')
and h.principal like concat('%',#{principal,jdbcType=VARCHAR},'%')
and h.status=#{status,jdbcType=VARCHAR}
and h.sourcing_type =#{sourcingType,jdbcType=VARCHAR}
and h.supplier_code in
#{item,jdbcType=INTEGER}
and h.is_delete=0
group by h.rfq_or_tender_form_code order by h.create_time desc

select h.`status`,h.rfq_or_tender_form_code, h.title,h.quotation_start_time, h.quotation_stop_time, h.supplier_code,h.principal, h.sourcing_type, h.way_of_quoting, h.way_to_find_the_source, h.company_code, css.name supplierName, h.currency, h.creator, h.supplier_code supplierCode from c_srm_enquiry_invitation_for_bids_t_h h left join c_srm_enquiry_invitation_for_bids_t_r cseifbtr on h.rfq_or_tender_form_code = cseifbtr.rfq_or_tender_form_code right join c_srm_supplier css on h.supplier_code = css.supplier_code join c_srm_purchase_demand_h cspdh on cseifbtr.purchase_request = cspdh.request_code
h.rfq_or_tender_form_code like concat('%',#{rfqOrTenderFormCode,jdbcType=VARCHAR},'%')
and h.title like concat('%',#{title,jdbcType=VARCHAR},'%')
and h.way_to_find_the_source=#{wayToFindTheSource,jdbcType=VARCHAR}
and h.status=#{status,jdbcType=VARCHAR}
and h.status in
#{item,jdbcType=INTEGER}
and h.supplier_code in
#{item,jdbcType=VARCHAR}
and cseifbtr.material_code like concat('%',#{materialCode,jdbcType=VARCHAR},'%')
and h.principal like concat('%',#{principal,jdbcType=VARCHAR},'%')
and css.name like concat('%',#{name,jdbcType=VARCHAR},'%')
and cspdh.buyer like concat('%',#{buyer,jdbcType=VARCHAR},'%')
and h.sourcing_type=#{sourcingType,jdbcType=VARCHAR}
and h.is_delete=0
group by h.rfq_or_tender_form_code order by h.create_time desc

