mapper.select:

select

from c_srm_purchase_order_r


        order_number=#{orderNumber,jdbcType=VARCHAR}


        and material_code=#{materialCode,jdbcType=VARCHAR}


        and id = #{id,jdbcType=INTEGER}

    and is_delete=0

group by material_code

select order_number purchaseOrderNo, line_item_no purLineItemNo, material_code, `count`, unit, update_time,department,remark,accessory_address,material_name,specification from c_srm_purchase_order_r where order_number in
#{item.orderNumber,jdbcType=VARCHAR}
and is_delete=0

